import React from "react";
import "./products.css";

const products = [
  {
    id: 1,
    name: "House Wire FR & HRFR–LSH",
    image: "house-wire.jpg", // Replace with the correct image path
  },
  {
    id: 2,
    name: "Networking LAN Cables",
    description: "(CAT5e Indoor, CAT5e Outdoor, CAT6, CAT6e Armoured, CAT6 Flat Traveling Cables)",
    image: "networking-lan.jpg",
  },
  {
    id: 3,
    name: "Transparent Speaker Cables",
    image: "speaker-cable.jpg",
  },
  {
    id: 4,
    name: "Jelly Filled Co-axial Cables",
    image: "coaxial-cable.jpg",
  },
  {
    id: 5,
    name: "Twin Twisted Silk Wires",
    image: "twisted-wires.jpg",
  },
  {
    id: 6,
    name: "Telecommunication Cables",
    image: "telecom-cables.jpg",
  },
  {
    id: 7,
    name: "CCTV Cables",
    image: "cctv-cables.jpg",
  },
  {
    id: 8,
    name: "Single & Multi-core Flexible Cables",
    image: "flexible-cables.jpg",
  },
];

const Products = () => {
  return (
    <>
   <section className="product-section">
    <div className="container">
    <div className="row">
      
      <div className="products-section p-5 col-md-12">
      <h2><b>OUR COTEGORIES</b></h2>
      <div className="products-grid">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.name} className="product-image" />
            <h3>{product.name}</h3>
            {product.description && <p>{product.description}</p>}
            <a href="#" className="view-more">
              →
            </a>
          </div>
        ))}
      </div>
      <button className="view-more-button">VIEW MORE</button>
    </div>
 

    </div>
    </div>

   </section>

<section>

<div className="carousel-container">
      <h3 className="carousel-header">OUR PRODUCTS</h3>
      <h2 className="carousel-title">Industry Leading Products</h2>
      <div className="carousel">
        {products.map((product, index) => (
          <div
            key={product.id}
            className={`carousel-item ${
              index === currentIndex ? "active" : ""
            }`}
          >
            <img src={product.image} alt={product.title} />
            <div className="product-info">
              <h4>{product.title}</h4>
              <span className="product-arrow">→</span>
            </div>
          </div>
        ))}
      </div>
      <div className="carousel-controls">
        <button className="control-button" onClick={handlePrev}>
          &#8592;
        </button>
        <button className="control-button" onClick={handleNext}>
          &#8594;
        </button>
      </div>
    </div>
  

</section>













