import React, { useState } from "react";
import "./Carousel.css";

const products = [
  {
    id: 1,
    title: "Transparent Speaker Cables",
    image: "image1.jpg", // Replace with the correct image path
  },
  {
    id: 2,
    title: "Jelly Filled Co-axial Cables",
    image: "image2.jpg", // Replace with the correct image path
  },
  {
    id: 3,
    title: "CCTV Cables",
    image: "image3.jpg", // Replace with the correct image path
  },
];

const Carousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === products.length - 1 ? 0 : prevIndex + 1
    );
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? products.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="carousel-container">
      <h3 className="carousel-header">OUR PRODUCTS</h3>
      <h2 className="carousel-title">Industry Leading Products</h2>
      <div className="carousel">
        {products.map((product, index) => (
          <div
            key={product.id}
            className={`carousel-item ${
              index === currentIndex ? "active" : ""
            }`}
          >
            <img src={product.image} alt={product.title} />
            <div className="product-info">
              <h4>{product.title}</h4>
              <span className="product-arrow">â†’</span>
            </div>
          </div>
        ))}
      </div>
      <div className="carousel-controls">
        <button className="control-button" onClick={handlePrev}>
          &#8592;
        </button>
        <button className="control-button" onClick={handleNext}>
          &#8594;
        </button>
      </div>
    </div>
  );
};

export default Carousel;
