import { useState } from "react";
import PageHeader from "../../Component/PageHeader/PageHeader";

export default function NumberingMachine() {
  const [show, setShow] = useState(false);

  const handleShow = () => setShow(true);
  const handleClose = () => setShow(false);

  return (
    <>
      <PageHeader topheading="Automatic Numbering" title="Numbering Machine" />
      <div className="container text-center mt-5">
        <button className="btn btn-primary" onClick={handleShow}>Open Form</button>
      </div>
      {show && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Fill the Form</h2>
            <form className="form">
              <input type="text" placeholder="Your Name" required className="form-input" />
              <input type="email" placeholder="Your Email" required className="form-input" />
              <button type="submit" className="btn btn-success">Submit</button>
            </form>
            <button className="close-button" onClick={handleClose}>Close</button>
          </div>
        </div>
      )}
    </>
  );
}
