import React from 'react';
import Slider from 'react-slick';
import "./hero.css";
import image2 from "../../images/Availabilty.webp";

const HeroSection = () => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 2000,
        fade: true, // Optional for fade effect between slides
    };

    return (
        <section className="hero-slider-sec">
            <div className="container-fluid">
            <Slider {...settings} className="herocarousel">
                {/* Slide 1 */}
                <div className="slide-item">
                    <div className="overlay">
                        <div className="carousel-content animate-text">
                            <h1>Welcome to Our Website</h1>
                            <p>Discover amazing features and opportunities.</p>
                        </div>
                    </div>
                    <img src="./images/slider2.png" className="animate-image" alt="Slide 1" />
                </div>

                {/* Slide 2 */}
                <div className="slide-item">
                    <div className="overlay">
                        <div className="carousel-content animate-text">
                            <h1>Explore New Horizons</h1>
                            <p>Join us in our journey of innovation and creativity.</p>
                        </div>
                    </div>
                    <img src="./images/slider1.png" className="animate-image" alt="Slide 2" />
                </div>

                {/* Slide 3 */}
                <div className="slide-item">
                    <div className="overlay">
                        <div className="carousel-content animate-text">
                            <h1>Build Your Dreams</h1>
                            <p>Take the first step toward achieving your goals today.</p>
                        </div>
                    </div>
                    <img src="./images/slider3.png" className="animate-image" alt="Slide 3" />
                </div>
            </Slider>
            </div>
        </section>
    );
};

export default HeroSection;
