import React from "react";
import Slider from "react-slick";
import "./testimonial.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Testimonial = () => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 3,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                }
            },
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                }
            }
        ]
    };

    const testimonials = [
        {
            id: 1,
            title: "Amazing Service",
            text: "This is an amazing service! Highly recommended for anyone.",
            image: "https://via.placeholder.com/300x200",
            link: "#"
        },
        {
            id: 2,
            title: "Great Quality",
            text: "The quality of work exceeded my expectations. Fantastic!",
            image: "https://via.placeholder.com/300x200",
            link: "#"
        },
        {
            id: 3,
            title: "Excellent Support",
            text: "The team was very supportive and responsive. 5 stars!",
            image: "https://via.placeholder.com/300x200",
            link: "#"
        },
        {
            id: 4,
            title: "Outstanding Experience",
            text: "I had an outstanding experience with this service.",
            image: "https://via.placeholder.com/300x200",
            link: "#"
        }
    ];

    return (
        <div className="slider-container">
            <Slider {...settings}>
                {testimonials.map((testimonial) => (
                    <div key={testimonial.id} className="testimonial-card">
                        <div className="card" style={{ width: "18rem" }}>
                            <img
                                src={testimonial.image}
                                className="card-img-top"
                                alt={testimonial.title}
                            />
                            <div className="card-body">
                                <h5 className="card-title">{testimonial.title}</h5>
                                <p className="card-text">{testimonial.text}</p>
                                <a href={testimonial.link} className="btn btn-primary">
                                    Learn More
                                </a>
                            </div>
                        </div>
                    </div>
                ))}
            </Slider>
        </div>
    );
};

export default Testimonial;













































