import React, { useState } from "react";
import "./products.css";
import pic1 from "../../images/product1.webp"
import pic2 from "../../images/product2.webp"
import pic3 from "../../images/product3.webp"


const products = [
  {
    id: 1,
    name: "House Wire FR & HRFR–LSH",
    image: "product1.webp", // Replace with the correct image path
  },
  {
    id: 2,
    name: "Networking LAN Cables",
    description: "(CAT5e Indoor, CAT5e Outdoor, CAT6, CAT6e Armoured, CAT6 Flat Traveling Cables)",
    image: "product2.webp",
  },
  {
    id: 3,
    name: "Transparent Speaker Cables",
    image: "speaker-cable.jpg",
  },
  {
    id: 4,
    name: "Jelly Filled Co-axial Cables",
    image: "coaxial-cable.jpg",
  },
  {
    id: 5,
    name: "Twin Twisted Silk Wires",
    image: "twisted-wires.jpg",
  },
  {
    id: 6,
    name: "Telecommunication Cables",
    image: "telecom-cables.jpg",
  },
  {
    id: 7,
    name: "CCTV Cables",
    image: "cctv-cables.jpg",
  },
  {
    id: 8,
    name: "Single & Multi-core Flexible Cables",
    image: "flexible-cables.jpg",
  },
];

const carouselItems = [
  {
    id: 1,
    title: "Transparent Speaker Cables",
    image: pic1, // Replace with the correct image path
  },
  {
    id: 2,
    title: "Jelly Filled Co-axial Cables",
    image: pic2, // Replace with the correct image path
  },
  {
    id: 3,
    title: "CCTV Cables",
    image: pic3, // Replace with the correct image path
  },
];

const Products = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Carousel navigation handlers
  const handleNext = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === carouselItems.length - 1 ? 0 : prevIndex + 1
    );
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? carouselItems.length - 1 : prevIndex - 1
    );
  };

  return (
    <>
      <section className="product-section">
        <div className="container">
          <div className="row">
            <div className="products-section p-5 col-md-12">
              <h2>
                <b>OUR CATEGORIES</b>
              </h2>
              <div className="products-grid">
                {products.map((product) => (
                  <div key={product.id} className="product-card">
                    <img
                      src={product.image}
                      alt={product.name}
                      className="product-image"
                    />
                    <h3>{product.name}</h3>
                    {product.description && <p>{product.description}</p>}
                    <a href="#" className="view-more">
                      →
                    </a>
                  </div>
                ))}
              </div>
              <button className="view-more-button">VIEW MORE</button>
            </div>
          </div>
        </div>
      </section>



    </>
  );
};

export default Products;
