import React, { useState, useEffect } from "react";
import "./products.css";

const products = [
  { id: 1, name: "Old products", image: "/images/products/s3.jpg" },
  { id: 2, name: "All New Printer Series", image: "/images/products/n4.jpg" },
  { id: 3, name: "Stamp Pad", image: "/images/products/sp1.jpg" },
  { id: 4, name: "INKS", image: "/images/products/i1.jpg" },
  { id: 5, name: "Paper Rolls", image: "/images/products/pr1.jpg" },
  { id: 6, name: "Toner Cartridges", image: "/images/products/tc1.jpg" },
  { id: 7, name: "Laser Printers", image: "/images/products/lp1.jpg" },
  { id: 8, name: "Office Supplies", image: "/images/products/os1.jpg" }
];

const Products = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isHovered, setIsHovered] = useState(false);

  useEffect(() => {
    if (!isHovered) {
      const interval = setInterval(() => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % products.length);
      }, 5000); // Slide one by one every 5 seconds (slower)
      return () => clearInterval(interval);
    }
  }, [isHovered]);

  const getVisibleProducts = () => {
    return products.slice(currentIndex, currentIndex + 4).concat(
      products.slice(0, Math.max(0, 4 - (products.length - currentIndex)))
    );
  };

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % products.length);
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + products.length) % products.length);
  };

  return (
    <section
      className="product-section"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="container">
        <div className="row justify-content-center">
          <button className="carousel-button prev small-button" onClick={handlePrev}>&#8592;</button>
          {getVisibleProducts().map((product) => (
            <div key={product.id} className="col-md-3 col-12">
              <div className="product-card text-center">
                <img
                  src={product.image}
                  alt={product.name}
                  className="product-image"
                />
                <h3>{product.name}</h3>
              </div>
            </div>
          ))}
          <button className="carousel-button next small-button" onClick={handleNext}>&#8594;</button>
        </div>
      </div>
    </section>
  );
};

export default Products;















