import React from "react";
import Slider from "react-slick";
import "./testimonial.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import img1 from "../../images/product1.webp"
import img2 from "../../images/product2.webp"
import img3 from "../../images/product3.webp"
// import img4 from "../../images/product4.webp"

const CustomPrevArrow = (props) => {
    const { className, style, onClick } = props;
    return (
        <div
            className={`${className} custom-prev-arrow`}
            style={{ ...style, display: "block", left: "-25px", zIndex: "1" }}
            onClick={onClick}
        />
    );
};

const CustomNextArrow = (props) => {
    const { className, style, onClick } = props;
    return (
        <div
            className={`${className} custom-next-arrow`}
            style={{ ...style, display: "block", right: "-25px", zIndex: "1" }}
            onClick={onClick}
        />
    );
};

const Testimonial = () => {
    const settings = {
        dots: true,
        infinite: true,
        autoplay: false,
        autoplaySpeed: 3000, // Scroll every 3 seconds
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        arrows: true,
        prevArrow: <CustomPrevArrow />,
        nextArrow: <CustomNextArrow />,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                }
            },
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                }
            }
        ]
    };

    const testimonials = [
        {
            id: 1,
            title: "Amazing Service",
            text: "This is an amazing service! Highly recommended for anyone.",
            image: img1,
            link: "#"
        },
        {
            id: 2,
            title: "Great Quality",
            text: "The quality of work exceeded my expectations. Fantastic!",
            image: img2,
            link: "#"
        },
        {
            id: 3,
            title: "Excellent Support",
            text: "The team was very supportive and responsive. 5 stars!",
            image: img3,
            link: "#"
        },
        {
            id: 4,
            title: "Outstanding Experience",
            text: "I had an outstanding experience with this service.",
            image: img1,
            link: "#"
        }
    ];

    return (
        <div className="slider-container">
            <Slider {...settings}>
                {testimonials.map((testimonial) => (
                    <div>
                        
                    </div>
                //    <div>
                //         <div key={testimonial.id} className="testimonial-card">
                //           <img
                //                 src={testimonial.image}
                //                 className="card-img-top"
                //                 alt={testimonial.title}
                //             />
                //            <div className="card" style={{ width: "18rem", margin: "0 auto" }}>
                           
                //                 <div className="card-body">
                //                 <h5 className="card-title">{testimonial.title}</h5>
                //                 <p className="card-text">{testimonial.text}</p>
                //                 <a href={testimonial.link} className="btn btn-">
                //                     Learn More
                //                 </a>
                //             </div>
                //         </div>
                //     </div>

                //    </div>
                ))}
            </Slider>
        </div>
    );
};

export default Testimonial;












































